"""
    Create a GNURadio readable text file of the first transmitted BAL frame
"""
import array
import scipy
import numpy


# frame as a long hex string stream
frame_string = ''
frame_data = frame_string.decode("hex")

# create byte array
array.array('B', frame_data)
map(ord, frame_data)

# create output file
file = open("/home/gs-laptop1/GroundStation/GroundStation/GNURadio/Test_Files/BAL_FEC_Test/frame.bit","w")
file.write(frame_data)
